import axios from 'axios';

export const fetchCryptoList = async() => {
    const { data } = await axios.get('https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=100&page=1&sparkline=false')
    return data.map((coin) => (
        {
            name: coin.name,
            priceChangePercent: coin.price_change_percentage_24h,
            image: coin.image,
            price: coin.current_price
        }
    ));
};

export const fetchCryptoName = async() => {
    const { data } = await axios.get('https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=100&page=1&sparkline=false')
    return data.map(coin => coin.name)
}

export const fecthCryptoFromSelected = async(coin) => {
    const { data } = await axios.get(`https://api.coingecko.com/api/v3/coins/${coin}?localization=false&tickers=false&market_data=true&community_data=false&developer_data=false&sparkline=false
    `)
    console.log(data);
}
