import styles from './CryptoList.module.css';
import { useState } from 'react';

const CryptoList = ({cryptoList, searchbarValue}) => {

    const [open, setOpen] = useState(false)
    const [close, setClose] = useState(true)
    console.log(close);

    const closeAndOpen = (index) => {
        setOpen(index) 
        setClose(!close)
        if(close){
            setOpen(false)
        }
    }

    if(!cryptoList.length){
        return <div>Loading...</div>
    }

    return (
        <div className={styles.container}>
            {/* wrapper */}
            <div>
                {cryptoList.filter((coin) => {
                    if(searchbarValue === ''){
                        return coin;
                    }
                    else if(coin.name.toLowerCase().includes(searchbarValue.toLowerCase())){
                        return coin;
                    }
                }).map((coin, index) => {
                    return (
                        // card
                        <div key={index} className={styles.card}>
                            <img className={styles.coinLogo} src={coin.image} alt="coin-logo"/>
                            <p className={styles.name}>{coin.name}</p>
                            <p className={styles.price}>{coin.price.toLocaleString('en-US', { style: 'currency', currency: 'USD' })}</p>
                            <p className={coin.priceChangePercent < 0 ? styles.negative : styles.positive}>{coin.priceChangePercent.toFixed(3)}%</p>
                            <i  onClick={() => {closeAndOpen(index)}} className={open === index ? 'fas fa-arrow-up' : 'fas fa-arrow-down'}></i>
                            <div className={open === index && close === true ? styles.showMore : styles.hide}>
                                <div className={styles.moreInformation}>
                                    <p>Market cap: {coin.marketCap.toLocaleString('en-US', {style: 'currency', currency: 'USD'})}</p>
                                    <p>High: {coin.high.toLocaleString('en-US', {style: 'currency', currency: 'USD'})}</p>
                                    <p>Low: {coin.low.toLocaleString('en-US', {style: 'currency', currency: 'USD'})}</p>
                                    <p>Circulation: {coin.circulation.toLocaleString('en-US')}</p>
                                </div>                               
                            </div>
                        </div>
                    );
                })}
            </div>
        </div>
    )
}

export default CryptoList;
